#include <iostream>
#include <cassert>

using namespace std;

const int L = 1;
const int W = 2;
const int D = 0;

int fnK(int& cnt_a, int& cnt_b, string a, string b)
{
  assert(a[0] == 'k');
  if (b[0] == 'k') {
    if (cnt_a == cnt_b) {
      cnt_a = cnt_b;
      return D;
    }
    if (cnt_a == 0) {      
      return L;
    }
    if (cnt_b == 0) {
      return W;
    }
    return cnt_a > cnt_b ? W : L;
  }
  if (b[0] == 't') {
    ++cnt_b;
    return cnt_a ? W : L;

  }
  if (b[0] == 'm') {
    if (cnt_a == 5) return W;
    return cnt_a ? D : L;
  }
  assert(false);
}

int fnM(int& cnt_a, int& cnt_b, string a, string b)
{
  assert(a[0] == 'm');
  if (b[0] == 'k') {
    if (cnt_b == 5) return L;
    return D;
  }
  if (b[0] == 't') {
    ++cnt_b;
    return D;
  }
  if (b[0] == 'm') {
    return D;
  }
  assert(false);
}

int fnT(int& cnt_a, int& cnt_b, string a, string b)
{
  assert(a[0] == 't');
  if (b[0] == 'k') {
    if (cnt_b) return L;
    return W;
  }
  if (b[0] == 'm') {
    ++cnt_a;
    return D;
  }
  if (b[0] == 't') {
    ++cnt_a;
    ++cnt_b;
    return D;
  }
  assert(false);
}

