      if (a[i][0] == 'k') {
	f = fnK(cnt_a, cnt_b, a[i], b[i]);
      }
      if (a[i][0] == 't') {
	f = fnT(cnt_a, cnt_b, a[i], b[i]);
      }
      if (a[i][0] == 'm') {
	f = fnM(cnt_a, cnt_b, a[i], b[i]);
      }

      if (a[i][0] == 'k') cnt_a = 0;
      if (b[i][0] == 'k') cnt_b = 0;
