int solve(){
  for(int i=0;i<K;i++){    
    if(A[i]=="kougekida"&&B[i]=="kougekida"){      
      if(a>b)return 1;
      else if(a<b)return 2;
      else a=b=0;
    }else if(A[i]=="kougekida"){
      if(a==0)return 2;
      else if(a==5)return 1;
      else if(B[i]=="tameru")return 1;
