      if(b==0)return 1;
      else if(b==5)return 2;
      else if(A[i]=="tameru")return 2;
      b=0;
    }else{
      if(A[i]=="tameru")a=max(5,a+1);
      if(B[i]=="tameru")b=max(5,b+1);
    }
  }
  return 0;
}

