	return false;
}
bool is_mamoru(string s) {
	if ("mamoru" == s) return true;
	return false;
}
bool is_kougekida(string s) {
	if ("kougekida" == s) return true;
	return false;
}
int play_func(string s1, string s2, int &p1, int &p2) {
	if (is_tameru(s1)) {
		p1 = min(5, p1+1);
	}
	if (is_tameru(s2)) {
		p2 = min(5, p2+1);
	}
	if (is_kougekida(s1) and is_kougekida(s2)) {
		if (p1 > p2) {
			return 1;
		}else if (p1 < p2) {
			return 0;
		}else {
			p1 = 0;
			p2 = 0;
			return 2;
		}
	}else if (is_kougekida(s1)) {
		if (p1 == 0) {
			return 0;
		}else if (is_mamoru(s2) and p1 == 5) {
			return 1;
		}else if (is_tameru(s2)) {
			return 1;
		}
		p1 = 0;
	}else if (is_kougekida(s2)) {
		if (p2 == 0) {
