  cin >> n;
  rep(i,2) rep(j,n) {
    string tmp;
    cin >> tmp;
    if( tmp[0] == 'm' ) opr[i][j] = BLK;
    if( tmp[0] == 't' ) opr[i][j] = CHG;
    if( tmp[0] == 'k' ) opr[i][j] = ATK;
  }
  compute();
  return 0;
}

