          }else{
            winner = 1;
            break;
          }
        }else if(power[1] == 0){
          winner = 0;
          break;
        }
        if(power[0] == power[1]){
          power[0] = power[1] = 0;
          continue;
        }else if(power[0] > power[1]){
          winner = 0;
          break;
        }else{
          winner = 1;
          break;
        }
      }else if(power[0] == 0){
        winner = 1;
        break;
