const string mamoru="mamoru";
const string tameru="tameru";
const string kougekida="kougekida";

const string winner[2]={"Isono-kun","Nakajima-kun"};
const string hikiwake="Hikiwake-kun";

string solve(vector<vs> hs)
{
	vi ps(2);
	rep(i,hs[0].size()){
		rep(j,2){
			if(hs[j][i]==tameru){
				if(hs[!j][i]==kougekida)
					return winner[!j];
			}
			if(hs[j][i]==mamoru){
				if(hs[!j][i]==kougekida){
					if(ps[!j]==5)
						return winner[!j];
				}
			}
			if(hs[j][i]==kougekida){
				if(ps[j]==0 && !(ps[!j]==0 && hs[!j][i]==kougekida))
					return winner[!j];
				if(hs[!j][i]==kougekida){
					if(ps[j]>ps[!j])
						return winner[j];
					if(ps[j]<ps[!j])
						return winner[!j];
				}
			}
		}
		rep(j,2){
			if(hs[j][i]==tameru) ps[j]=min(ps[j]+1,5);
			if(hs[j][i]==kougekida) ps[j]=0;
		}
	}
	return hikiwake;
}

