						this.game_result = false;
					}
					// ????????\????????¶????????????????????????
					isono[0].setForce(0);
					break;
				case ATTACK:
					// ??????????????????????????????????????????????????????????????????
					if (isono[0].getForce() > nakajima[0].getForce()) {
						// ?£????????????????
						nakajima[0].setHP(false);
					} else if (isono[0].getForce() < nakajima[0].getForce()) {
						// ?????¶????????????
						isono[0].setHP(false);
						this.game_result = false;
					}
					// ????????\????????¶????????????????????????
					isono[0].setForce(0);
					// ????????\????????¶????????????????????????
					nakajima[0].setForce(0);
					break;
