#define INF 2147483600

bool solve(){
  int n;
  cin>>n;
  vector<int> a(n+1),b(n+1);
  rep(i,n+1) cin>>a[i];
  rep(i,n+1) cin>>b[i];

  int sa=0,sb=0,edges=0;
  rep(i,n+1){
    sa += a[i];
    sb += b[i];
    edges += i * (a[i]-b[i]);
  }
  if(sa!=n || sb!=n || edges!=0) return false;

  vector<vector<int>> vec(n,vector<int>(n,0));

  vector<int> in(n), out(n);

  {
    int idx=0;
    for(int i=n; i>=0; i--) rep(_,a[i]) in[idx++]=i;

    idx = 0;
    for(int i=n; i>=0; i--) rep(_,b[i]) out[idx++]=i;
  }

  priority_queue<pair<int,int>> pq;
  // <left_edge, idx>

  rep(i,n) if(in[i]>0) pq.push(mp(in[i],i));

  rep(i,n){
    priority_queue<pair<int,int>> nxt;
    rep(_,out[i]){
      int p = pq.top().se; pq.pop();
      vec[i][p] = 1;
      in[p]--;
      if(in[p]>0) nxt.push(mp(in[p], p));
    }
    while(!pq.empty()){
      nxt.push(pq.top());
      pq.pop();
    }
    swap(nxt, pq);
  }
  assert(pq.empty());

  cout << "YES" << endl;
  rep(i,n) rep(j,n) cout << vec[i][j] << " \n"[j==n-1];

  return true;
}

int main(){
  if(solve()==false) cout << "NO" << endl;

  return 0;
}

