#include<bits/stdc++.h>
using namespace std;
int n;
int a[51],b[51];
int G[50][50];
int A[50],B[50];
typedef pair<int,int> P;

bool solve(){
  int ca=0,cb=0;
  for(int i=0;i<=n;i++){
    while(a[i]>0){
      A[ca++]=i;
      a[i]--;
    }
    while(b[i]>0){
      B[cb++]=i;
      b[i]--;
    }
  }
  if(ca!=cb||ca!=n)return false;
  sort(A,A+n);
  sort(B,B+n);
  reverse(A,A+n);
  reverse(B,B+n);

  for(int i=0;i<n;i++){
    vector<P> v;
    for(int j=0;j<n;j++)v.push_back( P(-B[j],j) );
    sort(v.begin(),v.end());
    for(int j=0;j<n;j++){
      int id=v[j].second;
      if(A[i]>0&&B[id]>0&&G[i][id]==0){
        A[i]--;
        B[id]--;
        G[i][id]++;
      }
    }
  }

  for(int i=0;i<n;i++)
    if(A[i]!=0||B[i]!=0)return false;
  return true;
}
int main(){
  cin>>n;
  for(int i=0;i<=n;i++)cin>>a[i];
  for(int i=0;i<=n;i++)cin>>b[i];
  if(solve()){
    cout<<"YES"<<endl;
    for(int i=0;i<n;i++){
      for(int j=0;j<n;j++){
        if(j)cout<<' ';
        cout<<G[i][j];
      }
      cout<<endl;
    }
  }else{
    cout<<"NO"<<endl;
  }
  return 0;
}

