signed main(void)
{
    cin.tie(0);
    ios::sync_with_stdio(false);

    int n;
    int num;
    int s1 = 0, s2 = 0;
    priority_queue<pair<int,int>> a, b;
    static int ans[MAX][MAX];

    cin>>n;

    for(int i = 0, sum = 0; i <= n; i++){
        cin>>num;
        s1 += num;
        for(int j = 0; j < num; j++){

            a.push(make_pair(i,sum++));

        }
    }

    for(int j = 0, sum = 0; j <= n; j++){
        cin>>num;
        s2 += num;
        for(int k = 0; k < num; k++){

            b.push(make_pair(j,sum++));

        }
    }

    if(s1 =! s2 || s1 != n){
        cout<<"NO"<<endl;
        return 0;
    }
    while(!a.empty()){
        pair<int,int> A = a.top(); a.pop();
        vector<pair<int,int>> temp;

        if(A.first == 0) break;

        for(int i = 0; i < A.first; i++){

            if(b.size() == 0){
                cout<<"NO"<<endl;
                return 0;
            }
            pair<int,int> B = b.top(); b.pop();

            if(B.first == 0){
                cout<<"NO"<<endl;
                return 0;
            }

            if(B.first-1 > 0)temp.push_back(make_pair(B.first-1,B.second));

            ans[B.second][A.second]++;
        }

        for(int j = 0; j < temp.size(); j++){
            b.push(temp[j]);
        }
    }

    cout<<"YES"<<endl;

    for(int i = 0; i < n; i++){
        for(int j = 0; j < n; j++){
            if(j) cout<<" ";
            cout<<ans[i][j];
        }
        cout<<endl;
    }

    return 0;
}

