void bl(){cout<<"NO"<<endl;exit(0);}
bool ch(){
  r(i,n)x1[i]=x2[i]=0;
  r(i,n)r(j,n)if(c[i][j])x1[i]++,x2[j]++;
  r(i,n)if(a1[i]!=x2[i])return 0;
  r(i,n)if(b1[i]!=x1[i])return 0;
  return 1;
}
main(){
  cin>>n;
  r(i,n+1)cin>>a[i],c1+=a[i],c3+=a[i]*i;
