  r(i,n+1)b2[i]=b[i];
  r(i,n+1)a2[i]=a[i];
  if(c1!=n||c2!=n||c3!=c4)bl();
  else for(;;){
    r(i,n+1)b[i]=b2[i];
    r(i,n+1)a[i]=a2[i];
    c1=c2=0;
    r(i,n+1)while(a[i])a1[c1++]=i,a[i]--;
    R(i,n+1)while(b[i])b1[c2++]=i,b[i]--;
    r(i,n){
      priority_queue<P>q;
      r(j,n)if(i!=j){
	if(rand()%2)q.push(P(a1[j],j));
	else q.push(P(a1[j],j*(-1)));
