	c[i][p.second]=1;
      }
    }
    if(ch())continue;
    cout<<"YES"<<endl;
    r(i,n){
      r(j,n){
	if(j)cout<<' ';
	cout<<c[i][j];
      }
      cout<<endl;
    }
    return 0;
  }
