  if(suma!=sumb||suma!=n)return false;
  int ca=0,cb=0;
  for(int i=0;i<=n;i++){
    while(a[i]>0){
      A[ca++]=i;
      a[i]--;
    }
    while(b[i]>0){
      B[cb++]=i;
      b[i]--;
    }
  }
  sort(A,A+n);
  sort(B,B+n);
  for(int i=0;i<n;i++){
    for(int j=0;j<n;j++){
      if(A[i]>0&&B[j]>0){
        G[i][j]++;
        A[i]--;
        B[j]--;
      }
    }
  }
  return true;
}
