typedef pair<int,int> pi;

void solve()
{
    int n;
    scanf(" %d", &n);

    vector<int> a(n+1),b(n+1);
    int suma=0,sumb=0;
    rep(i,n+1)
    {
        scanf(" %d", &a[i]);
        suma+=a[i];
    }
    rep(i,n+1)
    {
        scanf(" %d", &b[i]);
        sumb+=b[i];
    }

    // ??????
    if(suma!=n || sumb!=n)
    {
        printf("NO\n");
        return;
    }

    int edges=0;
    rep(i,n+1) edges += i*(a[i]-b[i]);

    if(edges!=0)
    {
        printf("NO\n");
        return;
    }

    int in[50],out[50];

    int idx=0;
    rep(i,n)
    {
        while(idx<=n && a[idx]==0) ++idx;

        in[i] = idx;
        --a[idx];
    }

    idx=0;
    rep(i,n)
    {
        while(idx<=n && b[idx]==0) ++idx;

        out[i] = idx;
        --b[idx];
