using namespace std;



#define NUM 51

struct Info{
	Info(int arg_node_id,int arg_num){
		node_id = arg_node_id;
		num = arg_num;
	}
	bool operator<(const struct Info & arg) const{ //残数の降順
		return num > arg.num;
	}

	int node_id,num;
};

int N;
int in_table[NUM],out_table[NUM],calc_out[NUM];
int edge_exist[NUM][NUM];
vector<int> G[NUM];


int main(){

	scanf("%d",&N);

	for(int i = 0; i < N; i++){
		for(int k = 0; k < N; k++)edge_exist[i][k] = 0;
	}

	int sum = 0,in_sum = 0;
	for(int i = 0; i <= N; i++){
		scanf("%d",&in_table[i]);
		sum += in_table[i];
		in_sum += i*in_table[i];
	}

	if(sum != N){ //ノードの合計がNでないならNO
		printf("NO\n");
		return 0;
	}

	sum = 0;
	int out_sum = 0;
	for(int i = 0; i <= N; i++){
		scanf("%d",&out_table[i]);
		sum += out_table[i];
		out_sum += i*out_table[i];
	}

	if(sum != N){ //ノードの合計がNでないならNO
		printf("NO\n");
		return 0;
	}

	if(in_sum != out_sum){ //入次数の合計と出次数の合計が合わなければNO
		printf("NO\n");
		return 0;
	}

	vector<Info> IN_NODE;
	int index = 0;

	//入次側のノードを仮想的に定める
	/*自己ループがOKなので、入次ノード側には、多重辺が不可という以外に制約がない(出元はどれでも良い)。
		 * よって、インデックスが若い順に役割(出次数を何個にするか、入次数を何個にするか)を割り振っても、結果には影響しない。
		ただし、辺の割り当て方(マッチング)には、少なくとも不適な手順が存在する模様 */
	for(int i = N; i >= 0; i--){ //★★降順に引いていった方がよさそう
		for(int k = 0; k < in_table[i]; k++){
			IN_NODE.push_back(Info(index++,i)); //★indexと書かれた入次側の端点をi個作るイメージ★
		}
	}

	index = 0;

	//出次側のノードを仮想的に定める
	for(int i = 0; i <= N; i++){
		for(int k = 0; k < out_table[i]; k++){
			calc_out[index++] = i; //★indexと書かれた出次側の端点をi個作るイメージ★
		}
	}

	/*for(int i = 0; i < N; i++){
		printf("calc_out[%d]:%d\n",i,calc_out[i]);
	}*/

	int count;

	for(int i = 0; i < N; i++){ //出次ノードのループ
		if(calc_out[i] == 0)continue;

		count = 0; //辺を張った数を計上
 		for(int k = 0; k < N; k++){ //入次ノード側のループ
 			if(IN_NODE[k].num == 0)break;
 			count++;
 			IN_NODE[k].num--;
 			G[i].push_back(IN_NODE[k].node_id);
 			if(count == calc_out[i])break;
		}

 		if(count < calc_out[i]){ //グラフ作成不可
 			printf("NO\n");
 			return 0;
 		}

 		sort(IN_NODE.begin(),IN_NODE.end()); //毎回降順に並べなおす
	}

	for(int i = 0; i < N; i++){
		for(int k = 0; k < G[i].size(); k++){
			edge_exist[i][G[i][k]] = 1;
		}
	}

	printf("YES\n");

	for(int i = 0; i < N; i++){
		printf("%d",edge_exist[i][0]);
		for(int k = 1; k < N; k++){
			printf(" %d",edge_exist[i][k]);
		}
		printf("\n");
	}

	return 0;
}

