void fail()
{
  cout << "NO" << endl;
  exit(0);
}

int main()
{
  int N, A[51] = {}, B[51] = {};
  bool graph[50][50] = {{}};

  cin >> N;
  for(int i = 0; i <= N; i++) cin >> A[i];
  for(int i = 0; i <= N; i++) cin >> B[i];

  if(accumulate(begin(A), end(A), 0) != N) fail();
  if(accumulate(begin(B), end(B), 0) != N) fail();
  vector< pair< int, int > > ventex;
  for(int i = 0; i <= N; i++) {
    while(B[i] > 0) {
      ventex.emplace_back(i, ventex.size());
      --B[i];
    }
  }

  int idx = 0;
  for(int i = N; i > 0; i--) {
    while(A[i] > 0) {
      sort(ventex.rbegin(), ventex.rend());
      int sz = i;
      for(auto &s : ventex) {
        if(s.first == 0) fail();
        --s.first;
        graph[idx][s.second] = true;
        if(--sz == 0) break;
      }
      --A[i];
      ++idx;
    }
  }

  cout << "YES" << endl;
  for(int i = 0; i < N; i++) {
    for(int j = 0; j < N; j++) {
      if(j > 0) cout << " ";
      cout << graph[j][i];
    }
    cout << endl;
  }
}

