		s1 += a[i];
	}
	sum2 = sum;
	for (int i = 0; i < n + 1; i++) {
		cin >> b[i];
		sum -= b[i] * i;
		s2 += b[i];
	}
	if (sum != 0 || s1 != n || s2 != n) {
		cout << "NO" << endl;
		return 0;
	}
	int Sum = 0;
	for (int i = 0; i < n + 1; i++) {
		for (int j = 0; j < a[i]; j++) {
			in[Sum] = i;
			Sum++;
		}
	}
	Sum = 0;
	for (int i = 0; i < n + 1; i++) {
		for (int j = 0; j < b[i]; j++) {
			out[Sum] = i;
			Sum++;
		}
	}

	//building edge
	G.resize(1 + n + n + 1);
	for (int i = 0; i < n; i++) {
		add_edge(0, i + 1, out[i]);
	}
