	n = in();
	for (i = 0; i <= n; i++) a[i] = in();
	for (i = 0; i <= n; i++) b[i] = in();

	for (i = 0; i <= n; i++) {
		for (j = 0; j < a[i]; j++) fr[fsz++] = i, sa += i;
		for (j = 0; j < b[i]; j++) to[tsz++] = i, sb += i;
