  val dosanko = readLine.trim.split(' ').map(_.toInt) match {case Array(s, p, c) ⇒ TeamParam(s, p, c); case _ ⇒ ???}
  val others = Array.tabulate(n - 1){_ ⇒ readLine.trim.split(' ').map(_.toInt) match {case Array(s, p, c) ⇒ TeamParam(s, p, c); case _ ⇒ ???}}
  val top8 = takeTop8(scores, others)
