vector<int> S(const vector<int>& r, const array<vector<int>, 3>& v) {
    const int n = v[0].size();
    array<vector<int>, 3> sv = v;
    REP(i, 3) sort(ALL(sv[i]));
    // rank
    array<vector<int>, 3> rank{vector<int>(n), vector<int>(n), vector<int>(n)};
    REP(i, 3) REP(j, n) {
        rank[i][j] = sv[i].end() - upper_bound(ALL(sv[i]), v[i][j]);
    }
    // score
    array<vector<int>, 3> score{vector<int>(n), vector<int>(n), vector<int>(n)};
    REP(i, 3) REP(j, n) {
        score[i][j] = r[rank[i][j]];
    }
    // sum
    vector<int> sum(n, 0);
    REP(i, n) {
        REP(j, 3) sum[i] += score[j][i];
    }
    return sum;
}

