#include <bits/stdc++.h>
using namespace std;

typedef vector<int> vi;

vi r;

bool calc(const vector<vi>& v){
	int n = v[0].size();
	vi score(n);
	vi idx(n);
	for(int i = 0; i < n; i++) idx[i] = i;
	for(int col = 0; col < 3; col++) {
		sort(idx.begin(), idx.end(), [&](const int a, const int b){return v[col][a] > v[col][b];});

		// cout<<endl;
		// for(int i = 0; i < n; i++) cout<<" "<<idx[i];//<<endl;
		// cout<<endl;
		score[idx[0]] += r[0];
		int cnt = 0;
		for(int i = 1; i < n; i++) {
			if(v[col][idx[i-1]] == v[col][idx[i]]) {
				cnt++;
				score[idx[i]] += r[i-cnt];
			}
			else {
				score[idx[i]] += r[i];
				cnt = 0;
			}
		}
	}
	sort(idx.begin(), idx.end(), [&](const int a, const int b){return score[a] == score[b] ? a < b: score[a] > score[b];});
	//for(int i = 0; i < n; i++) cout<<" "<<idx[i]<<endl;
	if(idx[n-1] == 0) return false;
	return true;
}

int main(){
	int N;
	cin >> N;
	r.resize(N);
	for(int i = 0; i < N; i++) cin >> r[i];
	vector<vi> v(3);
	for(int i = 0; i < 3; i++) {
		v[i].resize(N);
	}
	for(int j = 0; j < N; j++) for(int i = 0; i < 3; i++) cin >> v[i][j];
	int ans = 1e9;
	for(int i = 0; i < 3; i++) {
		for(int j = 1; j + v[i][0] <= 101; j++) {
			//cout<<""<<i<<" "<<j<<endl;
			v[i][0] += j;
			if(calc(v)) ans = min(ans, j);
			v[i][0] -= j;
		}
	}
	if(ans == 1e9) cout<<"Saiko"<<endl;
	else cout<<ans<<endl;
}

