typedef int _loop_int;
#define REP(i,n) for(_loop_int i=0;i<(_loop_int)(n);++i)
#define FOR(i,a,b) for(_loop_int i=(_loop_int)(a);i<(_loop_int)(b);++i)
#define FORR(i,a,b) for(_loop_int i=(_loop_int)(b)-1;i>=(_loop_int)(a);--i)
#define ALL(v) (v).begin(), (v).end()

int n;
int r[125];
int s[125],p[125],c[125];

bool check(int x,int y,int z){
  vi score(n,0);
  {
    vector<pii> yo(n);
    yo[0] = pii(x,0);
    FOR(i,1,n)yo[i] = pii(s[i],i);
    sort(ALL(yo));
    reverse(ALL(yo));
    int cur = 0;
    REP(i,n){
      if(i>0 && yo[i].first != yo[i-1].first)cur = i;
      score[yo[i].second] += r[cur];
    }
  }
  {
    vector<pii> yo(n);
    yo[0] = pii(y,0);
    FOR(i,1,n)yo[i] = pii(p[i],i);
    sort(ALL(yo));
    reverse(ALL(yo));
    int cur = 0;
    REP(i,n){
      if(i>0 && yo[i].first != yo[i-1].first)cur = i;
      score[yo[i].second] += r[cur];
    }
  }
  {
    vector<pii> yo(n);
    yo[0] = pii(z,0);
    FOR(i,1,n)yo[i] = pii(c[i],i);
    sort(ALL(yo));
    reverse(ALL(yo));
    int cur = 0;
    REP(i,n){
      if(i>0 && yo[i].first != yo[i-1].first)cur = i;
      score[yo[i].second] += r[cur];
    }
  }
  vector<pii> yo(n);
  REP(i,n)yo[i]  = pii(score[i],i);
  sort(ALL(yo));
  reverse(ALL(yo));
  REP(i,8)if(yo[i].second==0)return true;
  if(score[yo[7].second]==score[0])return true;
  return false;
}

int main(){
  scanf("%d",&n);
  REP(i,n)scanf("%d",r+i);
  REP(i,n)scanf("%d%d%d",s+i,p+i,c+i);
  int ans = 1e9;
  FOR(x,1,101){
    if(check(s[0]+x,p[0],c[0]))ans=min(ans,x);
  }
  FOR(x,1,101){
    if(check(s[0],p[0]+x,c[0]))ans=min(ans,x);
  }
