			int index(0);
			while (team_tmp[index].score != a)index++;
			dump(index);
			if (index <= 7)right = mid;
			else left = mid;

			dump(left, right);
		}
		if (right != 110)chmin(ans, right);
		dump(ans);

	}

	if (ans == INF)cout << "Saiko" << endl;
	else cout << ans << endl;

	return 0;
}

