signed main() {
	INIT;
	VAR(int, n);
	VEC(int, r, n);
	std::vector<PAIR> s(n), p(n), c(n);
	REP(i, n) {
		VAR(int, ss, pp, cc);
		s[i] = PAIR(ss, i);
		p[i] = PAIR(pp, i);
		c[i] = PAIR(cc, i);
	}

	auto getRank = [&]() {
		std::vector<int> sc(n, 0);
		{
			std::priority_queue<PAIR> pq;
			REP(i, n) pq.push(s[i]);
			int pre = -1;
			int cnt = -1;
			int pos = 0;
			while (!pq.empty()) {
				auto now = pq.top(); pq.pop();
				if (pre != now.first) {
					++cnt;
					CHMAX(cnt, pos);
				}
				sc[now.second] += r[cnt];
				pre = now.first;
				++pos;
			}
		}
		{
			std::priority_queue<PAIR> pq;
			REP(i, n) pq.push(p[i]);
			int pre = -1;
			int cnt = -1;
			int pos = 0;
			while (!pq.empty()) {
				auto now = pq.top(); pq.pop();
				if (pre != now.first) {
					++cnt;
					CHMAX(cnt, pos);
				}
				sc[now.second] += r[cnt];
				pre = now.first;
				++pos;
			}
		}
		{
			std::priority_queue<PAIR> pq;
			REP(i, n) pq.push(c[i]);
			int pre = -1;
			int cnt = -1;
			int pos = 0;
			while (!pq.empty()) {
				auto now = pq.top(); pq.pop();
				if (pre != now.first) {
					++cnt;
					CHMAX(cnt, pos);
				}
				sc[now.second] += r[cnt];
				pre = now.first;
				++pos;
			}
		}
		int t = sc[0];
		return n - std::distance(sc.begin(), std::lower_bound(ALL(sc), t + 1)) + 1;
	};
	int mis = INFINT;
	int mip = INFINT;
	int mic = INFINT;
	{
		int t = INFINT;
		std::swap(s[0].first, t);
		int fr = getRank();
		std::swap(s[0].first, t);
		if (fr != 9) {
			int l = 0, h = 100;
			while (h - l > 1) {
				int m = (l + h) / 2;
				s[0].first += m;
				int r = getRank();
				s[0].first -= m;
				if (r != 9) h = m;
				else l = m;
			}
			mis = h;
		}
	}
	{
		int t = INFINT;
		std::swap(p[0].first, t);
		int fr = getRank();
		std::swap(p[0].first, t);
		if (fr != 9) {
			int l = 0, h = 100;
			while (h - l > 1) {
				int m = (l + h) / 2;
				p[0].first += m;
				int r = getRank();
				p[0].first -= m;
				if (r != 9) h = m;
				else l = m;
			}
			mip = h;
		}
	}
	{
		int t = INFINT;
		std::swap(c[0].first, t);
		int fr = getRank();
		std::swap(c[0].first, t);
		if (fr != 9) {
			int l = 0, h = 100;
			while (h - l > 1) {
				int m = (l + h) / 2;
				c[0].first += m;
				int r = getRank();
				c[0].first -= m;
				if (r != 9) h = m;
				else l = m;
			}
			mic = h;
		}
	}
	if (mis == INFINT &&mip == INFINT &&mic == INFINT) {
		OUT("Saiko")BR;
	}
	else {
		OUT(std::min({ mis, mip, mic }))BR;
	}
	return 0;
}

