using namespace std;

struct Info{
	bool operator<(const struct Info &arg) const{
		return sum_points > arg.sum_points;
	};
	void set(int arg_id,int arg_smile,int arg_pure,int arg_cool){
		id = arg_id;
		smile = arg_smile;
		pure = arg_pure;
		cool = arg_cool;
	}
	int id,smile,pure,cool,sum_points;
};

int main(){

	int N;
	scanf("%d",&N);

	int point[N];
	for(int i = 0; i < N; i++)scanf("%d",&point[i]);

	int smile[N],pure[N],cool[N];

	vector<Info> V;

	for(int i = 0; i < N; i++){
		scanf("%d %d %d",&smile[i],&pure[i],&cool[i]);

		Info info;
		info.set(i+1,smile[i],pure[i],cool[i]);
		V.push_back(info);
	}

	sort(smile,smile+N,greater<int>());
	sort(pure,pure+N,greater<int>());
	sort(cool,cool+N,greater<int>());

	int rank_smile[101],rank_pure[101],rank_cool[101];

	int pre = BIG_NUM,num = -1;

	for(int i = 0; i < N; i++){
		if(smile[i]  < pre){
			num = i;
			rank_smile[smile[i]] = num;
		}else{ //smile[i] == pre
			rank_smile[smile[i]] = num;
		}
		pre = smile[i];
	}

	pre = BIG_NUM,num = -1;
	for(int i = 0; i < N; i++){
		if(pure[i]  < pre){
			num = i;
			rank_pure[pure[i]] = num;
		}else{ //smile[i] == pre
			rank_pure[pure[i]] = num;
		}
		pre = pure[i];
	}

	pre = BIG_NUM,num = -1;
	for(int i = 0; i < N; i++){
		if(cool[i]  < pre){
			num = i;
			rank_cool[cool[i]] = num;
		}else{ //cool[i] == pre
			rank_cool[cool[i]] = num;
		}
		pre = cool[i];
	}

	for(int i = 0; i < N; i++){
		V[i].sum_points = point[rank_smile[V[i].smile]]+point[rank_pure[V[i].pure]]+point[rank_cool[V[i].cool]];
	}

	sort(V.begin(),V.end());

	int index,sum;

	for(int i = 0; i < N; i++){
		if(V[i].id == 1){
			index = i;
			sum = V[i].sum_points;
			break;
		}
	}

	int smile_loc,pure_loc,cool_loc;
	for(int i = 0; i < N; i++){
		if(smile[i] == V[index].smile){
			smile_loc = i;
			break;
		}
	}
	for(int i = 0; i < N; i++){
		if(pure[i] == V[index].pure){
			pure_loc = i;
			break;
		}
	}
	for(int i = 0; i < N; i++){
		if(cool[i] == V[index].cool){
			cool_loc = i;
			break;
		}
	}


	int up_smile = BIG_NUM,up_pure = BIG_NUM,up_cool=BIG_NUM;
	int calc_rank_smile[101],calc_rank_pure[101],calc_rank_cool[101],calc_smile[N],calc_pure[N],calc_cool[N];

	for(int i = index-1; i >= 0; i--){
