					calc_rank_smile[calc_smile[k]] = num;
				}
				pre = calc_smile[k];
			}

			int new_self_points = point[calc_rank_smile[new_smile]]+point[rank_pure[V[index].pure]]+point[rank_cool[V[index].cool]];
			int new_enemy_points = point[calc_rank_smile[V[i].smile]]+point[rank_pure[V[i].pure]]+point[rank_cool[V[i].cool]];

			if(new_self_points >= new_enemy_points){
				up_smile = min(up_smile,new_smile-V[index].smile);
				break;
			}
		}

