			for(int k = 0; k < N; k++){
				if(calc_pure[k] < pre){
					num = k;
					calc_rank_pure[calc_pure[k]] = num;
				}else{
					calc_rank_pure[calc_pure[k]] = num;
				}
				pre = calc_pure[k];
			}

			int new_self_points = point[rank_smile[V[index].smile]]+point[calc_rank_pure[new_pure]]+point[rank_cool[V[index].cool]];
			int new_enemy_points = point[rank_smile[V[i].smile]]+point[calc_rank_pure[V[i].pure]]+point[rank_cool[V[i].cool]];

			if(new_self_points >= new_enemy_points){
				up_pure = min(up_pure,new_pure-V[index].pure);
				break;
			}
		}

		for(int new_cool = V[i].cool+1; new_cool <= 100; new_cool++){

			for(int k = 0; k < N; k++){
				calc_cool[k] = cool[k];
			}
			calc_cool[cool_loc] = new_cool;
			sort(calc_cool,calc_cool+N,greater<int>());

			pre = BIG_NUM,num = -1;
			for(int k = 0; k < N; k++){
				if(calc_cool[k] < pre){
					num = k;
					calc_rank_cool[calc_cool[k]] = num;
				}else{
					calc_rank_cool[calc_cool[k]] = num;
				}
				pre = calc_cool[k];
			}

			int new_self_points = point[rank_smile[V[index].smile]]+point[rank_pure[V[index].pure]]+point[calc_rank_cool[new_cool]];
			int new_enemy_points = point[rank_smile[V[i].smile]]+point[rank_pure[V[i].pure]]+point[calc_rank_cool[V[i].cool]];

			if(new_self_points >= new_enemy_points){
				up_cool = min(up_cool,new_cool-V[index].cool);
				break;
			}
		}
	}

	int ans = min(up_smile,min(up_pure,up_cool));

	if(ans == BIG_NUM){
		printf("Saiko\n");
	}else{
		printf("%d\n",ans);
	}

	return 0;
}

