        score = vector<int>(3);
    }
    int GetSum() {
        return score[0] + score[1] + score[2];
    }
};

bool Check(vector<Person> persons, int rule, const vector<int>& r) {
    int n = persons.size();
    // 順位再計算
    sort(allof(persons), [&](Person& lhs, Person& rhs) {
        return lhs.rpc[rule] > rhs.rpc[rule];
        });
    int rank = 0;
    int prev = -1;
    int add = 0;
    rep(i, n) {
        if (persons[i].rpc[rule] != prev) {
            rank += add + 1;
            add = 0;
            prev = persons[i].rpc[rule];
        }
        else {
            ++add;
        }
        persons[i].score[rule] = r[rank - 1];
    }

    sort(allof(persons), [&](Person& lhs, Person& rhs) {
        return lhs.GetSum() > rhs.GetSum();
        });

    rank = 0;
    prev = -1;
    add = 0;
    rep(i, n) {
        if (persons[i].GetSum() != prev) {
            rank += add + 1;
            add = 0;
            prev = persons[i].GetSum();
        }
        else {
            ++add;
        }
        //cout << persons[i].index << " rank : " << rank << " score : " << persons[i].GetSum() << endl;
        if (persons[i].index == 0) break;
    }
    return rank <= 8;
}

