        rep(i, n) {
            if (persons[i].rpc[j] != prev) {
                rank += add + 1;
                add = 0;
                prev = persons[i].rpc[j];
            }
            else {
                ++add;
            }
            persons[i].score[j] = r[rank - 1];
        }
    }

    // 元の順番に戻す
    sort(allof(persons), [&](Person& lhs, Person& rhs) {
