    for(int i=0; i<n; i++) {
        int x, y, z; cin >> x >> y >> z;
        a[i] = make_pair(x, i);
        b[i] = make_pair(y, i);
        c[i] = make_pair(z, i);
    }
    int ub = INIT, lb = -1;
    while(ub - lb > 1) {
        int mid = (ub + lb) / 2;
        if(d(mid)) ub = mid;
        else lb = mid;
    }
    if(ub < INIT) cout << ub << endl;
    else cout << "Saiko" << endl;
    return 0;
}

