template <class T> void chmin(T &a, const T &b) {
  if (b < a)
    a = b;
}

int main() {

  usize n;
  std::cin >> n;

  std::vector<u32> r(n);
  for (auto &e : r) {
    std::cin >> e;
  }

  std::vector<u32> s(n), p(n), c(n);
  for (usize i = 0; i != n; ++i) {
    std::cin >> s[i] >> p[i] >> c[i];
  }

  const auto get_rank = [&]() {
    std::vector<u32> pts(n, 0);
    for (const auto e_ : {&s, &p, &c}) {
      const auto &e = *e_;
      auto v = e;
      std::sort(v.begin(), v.end(), std::greater<>());
      std::map<u32, usize> rank;
      for (usize i = n; i != 0;) {
        --i;
        rank[v[i]] = i;
      }
      for (usize i = 0; i != n; ++i) {
        pts[i] += r[rank[e[i]]];
      }
    }
    auto v = pts;
    std::sort(v.begin(), v.end(), std::greater<>());
    std::map<u32, usize> rank;
    for (usize i = n; i != 0;) {
      --i;
      rank[v[i]] = i;
    }
    return rank[pts[0]];
  };

  static constexpr u32 inf = std::numeric_limits<u32>::max();
  u32 ans = inf;

  for (const auto e_ : {&s, &p, &c}) {
    auto &e = *e_;

    const u32 prev = e[0];
    for (u32 i = 1; i != 110; ++i) {
      e[0] += 1;
      if (get_rank() < 8) {
        chmin(ans, i);
        break;
      }
    }
    e[0] = prev;
  }

  if (ans != inf) {
    std::cout << ans << std::endl;
  } else {
    std::cout << "Saiko" << std::endl;
  }

  return 0;
}

