    vector<int> rank_S = calc_rank(S);
    vector<int> rank_C = calc_rank(C);
    vector<int> rank_P = calc_rank(P);

    vector<int> rank(S.size());
    vector<pii> score(S.size());
    for (int i = 0; i < N; i++) {
        score[i].first= R[rank_S[i]-1] + R[rank_C[i]-1] + R[rank_P[i]-1];
        score[i].second= i;
        // cerr << "score[" << i << "] = " << score[i].first << endl;
    }
    sort(score.begin(), score.end(), greater<pii>());

    rank[score[0].second] = 1;

    for (int i = 1; i < N; i++) {
        if (score[i].first == score[i-1].first)
            rank[score[i].second] = rank[score[i-1].second];
        else
            rank[score[i].second] = i + 1;

        if (score[i].second == 0)
            return rank[0];
    }

    assert(false);
    return INF;
}

vector<int> calc_rank(vector<pair<int, int>> status) {
    int N = status.size();

    sort(status.begin(), status.end(), greater<pii>());

    vector<int> rank(N);
    rank[status[0].second] = 1;

    for (int i = 1; i < N; i++) {
        if (status[i].first== status[i-1].first)
            rank[status[i].second] = rank[status[i-1].second];
        else
            rank[status[i].second] = i + 1;
    }

