#define IINF INT_MAX

struct Data {
  int v,ID;
  bool operator < ( const Data &data ) const {
    if( v != data.v ) return v > data.v;
    return ID < data.ID;
  }
};

int calc(vector<vector<int>> a,vector<int> &r) {
  int n = r.size();
  vector<vector<int>> score(3,vector<int>(n));
  rep(i,n) rep(j,3) score[j][i] = a[i][j];
  rep(i,3) sort(ALL(score[i]),greater<int>());

  vector<Data> vec;
  rep(i,n) {
    int v = 0;
    rep(j,3) {
      int rank = -1;
      rep(k,n) if( score[j][k] == a[i][j] ) { rank = k; break; }
      assert( rank != -1 );
      v += r[rank];
    }
    vec.push_back((Data){v,i});
  }
  sort(ALL(vec));
  rep(i,n) if( vec[i].ID == 0 ) return i+1;
  assert(false);
}

