				rep(k, N) cout << score[k].first << " ";
				cout << endl;
				dump(i);
				dump(v);
				chmin(min_cost, v);
				break;
			}
		}
	}
	if (min_cost == INF) cout << "Saiko" << endl;
	else cout << min_cost << endl;
}

