const int INF = 1LL << 29;
const ll LONGINF = 1LL << 60;
const ll MOD = 1000000007LL;

bool solve(vector< vector<int> > pts,
           int pa, int pb, int pc, vector<int> gain) {
    int N = pts[0].size();
    pts[0][0] += pa, pts[1][0] += pb, pts[2][0] += pc;

    vector<int> ord(N), sum(N);
    iota(ord.begin(), ord.end(), 0);
    for(int t=0; t<3; t++) {
        sort(ord.begin(), ord.end(), [&](int x, int y) {
                return pts[t][x] > pts[t][y];
            });

        int rank = -1, pre = -1;
        for(int i=0; i<N; i++) {
            if(pre != pts[t][ ord[i] ]) rank = i;
            sum[ ord[i] ] += gain[rank];
            pre = pts[t][ ord[i] ];
        }
    }

    sort(ord.begin(), ord.end(), [&](int x, int y) {
            return sum[x] > sum[y];
        });
    int rank = 0, pre;
    for(int i=0; i<N; i++) {
        if(pre != sum[ ord[i] ]) rank = i+1;
        if(ord[i] == 0) return rank < 9;
        pre = sum[ ord[i] ];
    }
    return false;
}

