    for(int p=1; p<=100; p++) {
        bool ok = false;
        ok |= solve(pts, p, 0, 0, gain);
        ok |= solve(pts, 0, p, 0, gain);
        ok |= solve(pts, 0, 0, p, gain);
        if(ok) {
            ans = p;
            break;
        }
    }
    if(ans == INF) {
        cout << "Saiko" << endl;
    }
    else {
        cout << ans << endl;
    }
    return 0;
}

