vector<pair<int, int>> calc_point(vector<pair<int, int>> v, vector<int> r, int x){
  int rank = 0;
  v[0].first += x;
  sort(v.begin(), v.end(), greater<pair<int, int>>());
  int pre = v[0].first;
  vector<pair<int, int>> point(v.size());
  int same = 0;
  for(int i = 0; i < N; i++){
    if(pre != v[i].first){
      rank+=same;
      same = 1;
    }else {
      same++;
    }
    int p = r[rank];
    point[i].first = p;
    point[i].second = v[i].second;
    pre = v[i].first;
  }
  sort(point.begin(), point.end(), mysort);
  return point;
}

vector<pair<int, int>> calc_sum(vector<pair<int, int>> s_p, vector<pair<int, int>> p_p, vector<pair<int, int>> c_p){
  vector<pair<int, int>> sum_p(N);
  for(int i = 0; i < N; i++){
    int sum = s_p[i].first + p_p[i].first + c_p[i].first;
    sum_p[i].first = sum;
    sum_p[i].second = -i;
  }
  sort(sum_p.begin(), sum_p.end(), greater<pair<int, int>>());
  return sum_p;
}

bool calc_rank(vector<pair<int, int>> sum_p){
  int rank;
  for(int i = 0; i < N; i++){
    if(sum_p[i].second == 0){
      rank = i;
      break;
    }
  }
  return rank < 8;
}

