typedef vector<int> vi;

vi r;

bool calc(const vector<vi>& v){
	int n = v[0].size();
	vi score(n);
	vi idx(n);
	for(int i = 0; i < n; i++) idx[i] = i;
	for(int col = 0; col < 3; col++) {
		sort(idx.begin(), idx.end(), [&](const int a, const int b){return v[col][a] > v[col][b];});

		// cout<<endl;
		// for(int i = 0; i < n; i++) cout<<" "<<idx[i];//<<endl;
		// cout<<endl;
		score[idx[0]] += r[0];
		int cnt = 0;
		for(int i = 1; i < n; i++) {
			if(v[col][idx[i-1]] == v[col][idx[i]]) {
				cnt++;
				score[idx[i]] += r[i-cnt];
			}
			else {
				score[idx[i]] += r[i];
				cnt = 0;
			}
		}
	}
	sort(idx.begin(), idx.end(), [&](const int a, const int b){return score[a] == score[b] ? a < b: score[a] > score[b];});
	//for(int i = 0; i < n; i++) cout<<" "<<idx[i]<<endl;
	for(int i = 0; i < 8; i++) if(idx[i] == 0) return true;
	return false;
}

