int num_of_gt_self_type(const int type, const int unit, const vector< vector<int> >& spc){
    int res = 0;
    int v = spc[unit][type];
    for(int i = 0; i < spc.size(); i++){
        if(v < spc[i][type]){
            res++;
        }
    }
    return res;
}

int num_of_gt_self(const vector<int>& pts){
    int res = 0;
    int v = pts[0];
    for(int i = 0; i < pts.size(); i++){
        if(v < pts[i]){
            res++;
        }
    }
    return res;
}

bool risable(const int N, const vector<int>& r, const vector< vector<int> >& spc){
    vector<int> pts(N, 0);

    for(int type = 0; type < 3; type++){
        for(int unit = 0; unit < N; unit++){
            int rank = num_of_gt_self_type(type, unit, spc);
            pts[unit] += r[rank];
        }
    }
    /*
    for(int i = 0; i < N; i++){
        cout << pts[i] << endl;
    }
    */

    return num_of_gt_self(pts) < 8;
}

