  rep(i,n) rep(j,3) {
    cin >> a[i][j];
    assert(  1 <= a[i][j] && a[i][j] <= 100 );
  }
  assert( calc(a,r) == 9 );
  int mini = IINF;
  REP(i,2,103) rep(j,3) {
    a[0][j] += i;
    if( calc(a,r) <= 8 ) { mini = i; goto Skip; }
    a[0][j] -= i;
  }
 Skip:;

  if( mini == IINF ) {
    puts("Saiko");
  } else {
    cout << mini << endl;
  }
  return 0;
}

