	for(int k = 0; k < N ;k ++){
		if(dosanko(k,0,s,p,c)){ress = k;break;}
	}
	for(int k = 0; k < N ;k ++){          
		if(dosanko(k,1,s,p,c)){resp = k;break;}
	}
	for(int k = 0; k < N ;k ++){
		if(dosanko(k,2,s,p,c)){resc = k;break;}
	}
