bool dosanko(int k, int para, int s[], int p[],int c[]){
	if(para == 0){s[0] += k; rs[0] = s[0];}
	if(para == 1){p[0] += k; rp[0] = p[0];}
	if(para == 2){c[0] += k; rc[0] = c[0];}

	sort(s,s+N);
	sort(p,p+N);
	sort(c,c+N);

	for(int i = 0;i < N;i++){
		for(int j = 0;j < N;j++){
			if(rs[i] == s[j] ) trs[i] = N - j -1;
			if(rp[i] == p[j] ) trp[i] = N - j -1;
			if(rc[i] == c[j] ) trc[i] = N - j -1;
		}
	}

	for(int i = 0; i < N;i++){
		tp[i] = r[trs[i]] + r[trp[i]] + r[trc[i]];
		sorttp[i] = tp[i];
	}
	sort(sorttp,sorttp+N);

	for(int i = 0;i < N;i++){
		for(int j = 0;j < N; j++){
			if(sorttp[j] == tp[i]) tr[i] = N - j;
		}
	}

	if(tr[0] == 8) return true;
	else return false;
}

