using vi = vector<int>;

const int LIM = 111;

int calc(int n, const vi &r, const vector<vi> &u, int p)
{
    int ng=0,ok=LIM;
    while(ok-ng>1)
    {
        int M = (ng+ok)/2;
        vector<vi> U(u);

        U[0][p]+=M;

        vi s(n);
        rep(i,3)
        {
            vi a(n);
            rep(j,n) a[j] = -U[j][i];
            sort(all(a));
            rep(j,n)
            {
                int idx = lower_bound(all(a),-U[j][i])-a.begin();
                s[j]-=r[idx];
            }
        }

        int score = s[0];
        sort(all(s));
        int rank = lower_bound(all(s),score)-s.begin()+1;
        if(rank<=8) ok=M;
        else ng=M;
    }
    return ok;
}

