map<int,int> mkRank(vector<int> A){
  map<int,int> rank,tmp;
  for(int a:A) rank[-a]++;
  tmp = rank;

  for(auto it=rank.begin(),it2=++rank.begin();it2!=rank.end();it++,it2++)
    it2->second += it->second;

  for(P const &p:rank) rank[-p.first] = rank[p.first]-tmp[p.first];
  return rank;
}

int getScore(map<int,int> rank,int a){return r[rank[a]];}

vector<int> calcTotalScore(vector<int> a,vector<int>b,vector<int>c){
  vector<int> res(n,0);
  map<int,int> A = mkRank(a);
  map<int,int> B = mkRank(b);
  map<int,int> C = mkRank(c);
  for(int i=0;i<n;i++) res[i] = getScore(A,a[i]) + getScore(B,b[i]) + getScore(C,c[i]);
  return res;
}

int calc(vector<int> a,vector<int>b,vector<int>c){
  int ans = 1e9;
  for(int i=0;i<200;i++){
