#define rep(i,n) for(int i = 0;i < n;i++)

typedef pair<int, int> P;
int n;

bool ok(vector<int> ts, vector<int> tp, vector<int> tc, vector<int> r) {

	vector<P> s(n), p(n), c(n);

	rep(i, n) {
		s[i] = P(ts[i], i);
	}
	rep(i, n) {
		p[i] = P(tp[i], i);
	}
	rep(i, n) {
		c[i] = P(tc[i], i);
	}

	sort(s.begin(), s.end()); reverse(s.begin(), s.end());
	sort(p.begin(), p.end()); reverse(p.begin(), p.end());
	sort(c.begin(), c.end()); reverse(c.begin(), c.end());

	vector<int> score(n, 0);
	int sk = 0;
	int st = 0;
	rep(i, n) {
		int id = s[i].second;
		if (i == 0) {
			score[id] += r[sk++];
		}
		else {
			if (s[i].first == s[i - 1].first) {
				score[id] += r[sk - 1]; st++;
			}
			else {
				if (st) { sk += st; st = 0; }
				score[id] += r[sk++];
			}
		}
	}
	int pk = 0;
	int pt = 0;
	rep(i, n) {
		int id = p[i].second;
		if (i == 0) {
			score[id] += r[pk++];
		}
		else {
			if (p[i].first == p[i - 1].first) {
				score[id] += r[pk - 1]; pt++;
			}
			else {
				if (pt) { pk += pt; pt = 0; }
				score[id] += r[pk++];
			}
		}
	}
	int ck = 0;
	int ct = 0;
	rep(i, n) {
		int id = c[i].second;
		if (i == 0) {
			score[id] += r[ck++];
		}
		else {
			if (c[i].first == c[i - 1].first) {
				score[id] += r[ck - 1]; ct++;
			}
			else {
				if (ct) { ck += ct; ct = 0; }
				score[id] += r[ck++ + ct];
			}
		}
	}

	vector<P> rank(n);
	rep(i, n) {
		rank[i] = P(score[i], i);
	}
	sort(rank.begin(), rank.end());
	reverse(rank.begin(), rank.end());

	int rt = 0; int rtm = 0;
	for (int i = 0; i < n;i++) {
		if (i == 0) {
			rt = 1;
		}
		else {
			if (rank[i].first == rank[i - 1].first) {
				rtm++;
			}
			else {
				if (rtm) { rt += rtm; rtm = 0; }
				rt++;
			}
		}
		if (rank[i].second == 0) {
			return rt <= 8;
		}
	}


}


int main() {

	cin >> n;
	vector<int> r(n);
	rep(i, n) {
		cin >> r[i];
	}

	vector<int> s(n), p(n), c(n);
	rep(i, n) {
		cin >> s[i] >> p[i] >> c[i];
	}
	int ans = 1e9;

	for (int i = 0; i + s[0] <= 101; i++) {
		int ns = i + s[0];
		swap(s[0], ns);
		if (ok(s, p, c, r)) {
			ans = min(i, ans);
		}
		swap(s[0], ns);
	}
	for (int i = 0; i + p[0] <= 101; i++) {
		int ns = i + p[0];
		swap(p[0], ns);
		if (ok(s, p, c, r)) {
			ans = min(i, ans);
		}
		swap(p[0], ns);
	}
	for (int i = 0; i + c[0] <= 101; i++) {
		int ns = i + c[0];
		swap(c[0], ns);
		if (ok(s, p, c, r)) {
			ans = min(i, ans);
		}
		swap(c[0], ns);
	}

	if (ans == 1e9) {
		cout << "Saiko" << endl;
	}
	else {
		cout << ans << endl;
	}

	return 0;
}

