//自分より真に大きいのが7人以下
//取る点としては、nかn+1だけを考慮すればよい

int n;
int s[110],t[110];
int a[3][110],x[110];

void f(int*p){
	for(int i=0;i<n;i++){
		int cnt=0;
		for(int j=0;j<n;j++)if(p[j]>p[i])cnt++;
		t[i]=x[cnt];
	}
}

int main(){
	scanf("%d",&n);
	for(int i=0;i<n;i++)scanf("%d",x+i);
	for(int i=0;i<n;i++)scanf("%d%d%d",a[0]+i,a[1]+i,a[2]+i);
	int ans=1000;
	for(int k=0;k<3;k++){
		for(int i=0;i<n;i++)s[i]=0;
		for(int kk=0;kk<3;kk++)if(kk!=k){
			f(a[kk]);
			for(int i=0;i<n;i++)s[i]+=t[i];
		}

		int ori=a[k][0];
		for(int i=1;i<n;i++){
			for(int d=0;d<2;d++)if(a[k][i]+d>ori){
				a[k][0]=a[k][i]+d;
				f(a[k]);
				int cnt=0;
				for(int j=0;j<n;j++)if(s[j]+t[j]>s[0]+t[0])cnt++;
				if(cnt<=7)ans=min(ans,a[k][0]-ori);
			}
		}
		a[k][0]=ori;
	}
	if(ans==1000)puts("Saiko");
	else printf("%d\n",ans);
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.NoSuchElementException;

public class Main implements Runnable {
	static final int INF = (int)1e9+7;
	int N;
	int[] R,S,P,C;

	public boolean check(int x,int y,int z){
		int[] sum = new int[N];

		int[] rank_s = new int[N],rank_p = new int[N],rank_c = new int[N];
		int[] rank_all = new int[N];

		//smile
		for(int i = 0;i < N;i++){
			for(int j = i+1;j < N;j++){
				int add = 0;
				if(i == 0)add = x;
				if(S[i]+add < S[j])rank_s[i]++;
				else if(S[i]+add > S[j])rank_s[j]++;
			}
		}
		//pure
		for(int i = 0;i < N;i++){
			for(int j = i+1;j < N;j++){
				int add = 0;
				if(i == 0)add = y;
				if(P[i]+add < P[j])rank_p[i]++;
				else if(P[i]+add> P[j])rank_p[j]++;
			}
		}
		//cool
		for(int i = 0;i < N;i++){
			for(int j = i+1;j < N;j++){
				int add = 0;
				if(i == 0)add = z;
				if(C[i]+add < C[j])rank_c[i]++;
				else if(C[i]+add > C[j])rank_c[j]++;
			}
		}

		for(int i = 0;i < N;i++){
			sum[i] = R[rank_s[i]] + R[rank_p[i]] + R[rank_c[i]];
		}

		for(int i = 0;i < N;i++){
			for(int j = i+1;j < N;j++){
				if(sum[i] < sum[j])rank_all[i]++;
				else if(sum[i] > sum[j])rank_all[j]++;
			}
		}

		return rank_all[0] <= 7;
	}

	public void solve() {
		N = nextInt();

		R = new int[N];
		S = new int[N];
		P = new int[N];
		C = new int[N];
		for(int i = 0;i < N;i++){
			R[i] = nextInt();
		}

		for(int i = 0;i < N;i++){
			S[i] = nextInt();
			P[i] = nextInt();
			C[i] = nextInt();
		}

		int low = 0;
		int high = INF;
		for(int i = 0;i < 100;i++){
			int mid = high+low>>1;
			if(check(mid,0,0) || check(0,mid,0) || check(0,0,mid)){
				high = mid;
			}else{
				low = mid;
			}
		}
		if(high == INF)out.println("Saiko");
		else out.println(high);


	}

	public static void main(String[] args) {
		new Thread(null, new Main(), "", 64 * 1024 * 1024).start();
	}

	/* Input */
	private static final InputStream in = System.in;
	private static final PrintWriter out = new PrintWriter(System.out);
	private final byte[] buffer = new byte[2048];
	private int p = 0;
	private int buflen = 0;

	private boolean hasNextByte() {
		if (p < buflen)
			return true;
		p = 0;
		try {
			buflen = in.read(buffer);
		} catch (IOException e) {
			e.printStackTrace();
		}
		if (buflen <= 0)
			return false;
		return true;
	}

	public boolean hasNext() {
		while (hasNextByte() && !isPrint(buffer[p])) {
			p++;
		}
		return hasNextByte();
	}

	private boolean isPrint(int ch) {
		if (ch >= '!' && ch <= '~')
			return true;
		return false;
	}

	private int nextByte() {
		if (!hasNextByte())
			return -1;
		return buffer[p++];
	}

	public String next() {
		if (!hasNext())
			throw new NoSuchElementException();
		StringBuilder sb = new StringBuilder();
		int b = -1;
		while (isPrint((b = nextByte()))) {
			sb.appendCodePoint(b);
		}
		return sb.toString();
	}

	public int nextInt() {
		return Integer.parseInt(next());
	}

	public long nextLong() {
		return Long.parseLong(next());
	}

	public double nextDouble() {
		return Double.parseDouble(next());
	}

	@Override
	public void run() {
		out.flush();
		new Main().solve();
		out.close();

	}
}

import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.Comparator;
import java.util.NoSuchElementException;

public class Main {

  int N;
  int[] R;
  int[][] spc;

  private boolean ok(int x, int pos) {
    spc[0][pos] += x;

    int[][] points = new int[N][2];
    for (int team = 0; team < 3; team++) {

      int[][] tmp = new int[N][2];
      for (int i = 0; i < N; i++) {
        tmp[i][0] = spc[i][team];
        tmp[i][1] = i;
      }

      Arrays.sort(tmp, new Comparator<int[]>() {
        @Override
        public int compare(int[] o1, int[] o2) {
          return o2[0] - o1[0];
        }
      });

      int rank = 0;
      for (int i = 0; i < N; i++) {
        if (i > 0 && tmp[i][0] != tmp[i - 1][0]) {
          rank = i;
        }
        int unit = tmp[i][1];
        points[unit][0] += R[rank];
        points[unit][1] = unit;
      }
    }

    Arrays.sort(points, new Comparator<int[]>() {
      @Override
      public int compare(int[] o1, int[] o2) {
        if (o1[0] == o2[0]) {
          return o1[1] - o2[1];
        }
        return o2[0] - o1[0];
      }
    });

    spc[0][pos] -= x;
    for (int i = 0; i < N; i++) {
      if (points[i][1] == 0) {
        return i < 8;
      }
    }
    throw new IllegalArgumentException();
  }

  private void solve(FastScanner in, PrintWriter out) {
    N = in.nextInt();
    R = in.nextIntArray(N);
    spc = new int[N][3];
    for (int i = 0; i < N; i++) {
      spc[i][0] = in.nextInt();
      spc[i][1] = in.nextInt();
      spc[i][2] = in.nextInt();
    }

    int min = 10000;
    for (int pos = 0; pos < 3; pos++) {
      for (int i = 0; i < 200; i++) {
        if (ok(i, pos)) {
          min = Math.min(min, i);
          break;
        }
      }
    }
    if (min == 10000) {
      out.println("Saiko");
      return;
    }

    out.println(min);
  }

  public static void main(String[] args) {
    PrintWriter out = new PrintWriter(System.out);
    new Main().solve(new FastScanner(), out);
    out.close();
  }

  private static class FastScanner {

    private final InputStream in = System.in;
    private final byte[] buffer = new byte[1024];
    private int ptr = 0;
    private int bufferLength = 0;

    private boolean hasNextByte() {
      if (ptr < bufferLength) {
        return true;
      } else {
        ptr = 0;
        try {
          bufferLength = in.read(buffer);
        } catch (IOException e) {
          e.printStackTrace();
        }
        if (bufferLength <= 0) {
          return false;
        }
      }
      return true;
    }

    private int readByte() {
      if (hasNextByte()) {
        return buffer[ptr++];
      } else {
        return -1;
      }
    }

    private static boolean isPrintableChar(int c) {
      return 33 <= c && c <= 126;
    }

    private void skipUnprintable() {
      while (hasNextByte() && !isPrintableChar(buffer[ptr])) {
        ptr++;
      }
    }

    boolean hasNext() {
      skipUnprintable();
      return hasNextByte();
    }

    public String next() {
      if (!hasNext()) {
        throw new NoSuchElementException();
      }
      StringBuilder sb = new StringBuilder();
      int b = readByte();
      while (isPrintableChar(b)) {
        sb.appendCodePoint(b);
        b = readByte();
      }
      return sb.toString();
    }

    long nextLong() {
      if (!hasNext()) {
        throw new NoSuchElementException();
      }
      long n = 0;
      boolean minus = false;
      int b = readByte();
      if (b == '-') {
        minus = true;
        b = readByte();
      }
      if (b < '0' || '9' < b) {
        throw new NumberFormatException();
      }
      while (true) {
        if ('0' <= b && b <= '9') {
          n *= 10;
          n += b - '0';
        } else if (b == -1 || !isPrintableChar(b)) {
          return minus ? -n : n;
        } else {
          throw new NumberFormatException();
        }
        b = readByte();
      }
    }

    double nextDouble() {
      return Double.parseDouble(next());
    }

    double[] nextDoubleArray(int n) {
      double[] array = new double[n];
      for (int i = 0; i < n; i++) {
        array[i] = nextDouble();
      }
      return array;
    }

    double[][] nextDoubleMap(int n, int m) {
      double[][] map = new double[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextDoubleArray(m);
      }
      return map;
    }

    public int nextInt() {
      return (int) nextLong();
    }

    public int[] nextIntArray(int n) {
      int[] array = new int[n];
      for (int i = 0; i < n; i++) {
        array[i] = nextInt();
      }
      return array;
    }

    public long[] nextLongArray(int n) {
      long[] array = new long[n];
      for (int i = 0; i < n; i++) {
        array[i] = nextLong();
      }
      return array;
    }

    public String[] nextStringArray(int n) {
      String[] array = new String[n];
      for (int i = 0; i < n; i++) {
        array[i] = next();
      }
      return array;
    }

    public char[][] nextCharMap(int n) {
      char[][] array = new char[n][];
      for (int i = 0; i < n; i++) {
        array[i] = next().toCharArray();
      }
      return array;
    }

    public int[][] nextIntMap(int n, int m) {
      int[][] map = new int[n][];
      for (int i = 0; i < n; i++) {
        map[i] = nextIntArray(m);
      }
      return map;
    }
  }
}
