int main() {
  ll n,m;
  cin >> n >> m;
  vector<P> v[n];
  for(int i=0; i<m; i++) {
    int x,y,z;
    cin >> x >> y >> z;
    x--,y--;
    v[x].push_back(P(y,z));
    v[y].push_back(P(x,z));
  }
  ll v0,A,B,C;
  cin >> v0 >> A >> B >> C;
  ll d[n][50];
  for(int i=0; i<n; i++)for(int j=0; j<C; j++) d[i][j]=1LL<<59;
  d[0][v0]=0;
  priority_queue<PP,vector<PP>,greater<PP> > que;
  que.push(PP(0,P(0,v0)));
  while(!que.empty()) {
    PP p=que.top();que.pop();
    ll x=p.second.first,t=p.second.second,c=p.first;
    if(d[x][t]<c) continue;
    for(int i=0; i<v[x].size(); i++) {
      ll y=v[x][i].first,cc=v[x][i].second;
      ll s=(A*t+B)%C;
      if(d[y][s]<=d[x][t]+cc*t) continue;
      d[y][s]=d[x][t]+cc*t;
      que.push(PP(d[y][s],P(y,s)));
    }
  }
  for(int i=0; i<n-1; i++)for(int j=0; j<C; j++)d[i][j]=1LL<<59;
  for(int i=0; i<C; i++) que.push(PP(d[n-1][i],P(n-1,i)));
  while(!que.empty()) {
    PP p=que.top();que.pop();
    ll x=p.second.first,t=p.second.second,c=p.first;
